var async = require('async'),
	fs = require('fs');

var files = [
	'node_modules/marked/lib/marked.js', 
	'node_modules/diff/diff.js', 
	'node_modules/markdown-list-types/list-types.js', 
	'node_modules/simple-markdown-tables/simple_tables.js',
	'index.js'
	];

async.map(files, fs.readFile, function(err, contents) {
  var output = '';
  files.forEach(function(file, index) {
    output += '// ' + files[index] + '\n' + contents[index] + '\n';
  });
  fs.writeFile('markdown-editor.js', output);
});