var assert = require('assert');
var listTypes = require('./list-types.js');

var input = [
  'a. One',
  'b. Two',
  'c. Three',
  'd. Four',
  'e. Five',
  'f. Six',
  'g. Seven',
  'h. Eight',
  'i. One -- see the README'
].join('\n');
var expected = [
  '1. One',
  '2. Two',
  '3. Three',
  '4. Four',
  '5. Five',
  '6. Six',
  '7. Seven',
  '8. Eight',
  '1. One -- see the README'
].join('\n');
assert.equal(listTypes(input), expected);

var input = [
  'i. One',
  'ii. Two',
  'iii. Three',
  'iv. Four',
  'v. Five',
  'vi. Six',
  'vii. Seven',
  'viii. Eight',
  'ix. Nine',
  'x. Ten',
  'xi. Eleven'
].join('\n');
var expected = [
  '1. One',
  '2. Two',
  '3. Three',
  '4. Four',
  '5. Five',
  '6. Six',
  '7. Seven',
  '8. Eight',
  '9. Nine',
  '10. Ten',
  '11. Eleven'
].join('\n');
assert.equal(listTypes(input), expected);

var input = [
  '1. First level',
  '    a. Second level one',
  '    b. Second level two',
  '2. First level two',
  '    a. Second level three',
  '        i. Third level',
  '        ii. Third level'
].join('\n');
var expected = [
  '1. First level',
  '    1. Second level one',
  '    2. Second level two',
  '2. First level two',
  '    1. Second level three',
  '        1. Third level',
  '        2. Third level'
].join('\n');
assert.equal(listTypes(input), expected);

// don't treat it like a list if there is no content
var input = [
  'a.',
  'b.'
].join('\n');
assert.equal(listTypes(input), input);

var input = [
  'a. ',
  'b. '
].join('\n');
assert.equal(listTypes(input), input);

// treat it like a list if there *is* content; even a space
var input = [
  'a.  ',
  'b.  '
].join('\n');
var expected = [
  '1.  ',
  '2.  '
].join('\n');
assert.equal(listTypes(input), expected);

console.log('All tests passed.');